import React, { useState, useEffect, useRef } from 'react';
import GoogleMapReact from 'google-map-react';
import './css/GoogleMap.css';

const AnyReactComponent = ({ text, onClick, id }) => (
  <div style={{ position: 'relative' }}>
    <img
      src="/pin.png"
      alt=""
      className="custom-marker"
      onClick={() => onClick(id)}
    />
    {text}
  </div>
);

const GoogleMap = () => {
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [selectedText, setSelectedText] = useState('');

  const mapContainerRef = useRef(null);

  const defaultProps = {
    center: {
      lat: 4.652024891189568,
      lng: -74.09331545257302,
    },
    zoom: 14.3,
    draggable: false,
  };

  const markers = [
    {
      id: 1,
      lat: 4.641542013636317,
      lng: -74.09167213103521,
      text: 'Text for Marker 1',
    },
    {
      id: 2,
      lat: 4.639303311132185,
      lng: -74.09792046924814,
      text: 'Text for Marker 2',
    },
    // Add more markers as needed
  ];

  const handleMarkerClick = (markerId) => {
    setSelectedMarker(markerId);
    const selectedMarkerData = markers.find((marker) => marker.id === markerId);
    if (selectedMarkerData) {
      setSelectedText(selectedMarkerData.text);
    }
  };

  const handleMapClick = (e) => {
    // Check if the click target is the map container or one of its children
    if (mapContainerRef.current && !mapContainerRef.current.contains(e.target)) {
      setSelectedMarker(null); // Clear selected marker when clicking outside the markers
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      setSelectedMarker(null); // Clear selected marker when the "ESC" key is pressed
    }
  };

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    window.addEventListener('keydown', handleKeyDown);

    // Cleanup effect on unmount
    return () => {
      document.body.style.overflow = 'visible';
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  return (
    <div className="map-container" ref={mapContainerRef} onClick={handleMapClick}>
      <GoogleMapReact
        bootstrapURLKeys={{
          key: 'AIzaSyBoEQde7SsgSK7o3C89akbT1Z6dDl2T6nA',
        }}
        defaultCenter={defaultProps.center}
        defaultZoom={defaultProps.zoom}
        draggable={defaultProps.draggable}
      >
        {markers.map((marker) => (
          <AnyReactComponent
            key={marker.id}
            lat={marker.lat}
            lng={marker.lng}
            id={marker.id}
            onClick={handleMarkerClick}
          />
        ))}
      </GoogleMapReact>

      {selectedMarker !== null && (
        <div className="banner">
          {selectedText}
        </div>
      )}
    </div>
  );
};

export default GoogleMap;
