import React, { useState } from 'react';
import FacebookLogin from 'react-facebook-login';
import { Navigate } from 'react-router-dom';
import './css/FacebookLoginComponent.css';


const FacebookLoginComponent = () => {
    const apiUrl = process.env.REACT_APP_API_URL;
    const facebook_secret = process.env.REACT_APP_SECRET_FACEBOOK;
    const [redirectToSecurePage, setRedirectToSecurePage] = useState(false);

    const responseFacebook = (response) => {
      console.log(response);

      fetch(apiUrl+'/user/auth/facebook', {
        credentials: 'include',
      })
          .then((response) => response.json())
          .then((data) => {
              console.log(data);

              if (data && data.secure_data) {
                  console.log("Good");
                  setRedirectToSecurePage(true);
              }
          })
          .catch((error) => {
              console.error('Error:', error);
          });
    };

    return (
        <div className="facebook-login-component">
            <div className="icon-container">
                <img src="/facebook.svg"  alt={""}/>
            </div>
            <FacebookLogin
                appId={String(facebook_secret)}
                autoLoad={false}
                fields="name,email,picture"
                callback={responseFacebook}
                cssClass="facebook-login-button"
            />
            {redirectToSecurePage && <Navigate to="/user" />}
        </div>
    );
};

export default FacebookLoginComponent;
