import React, { useState, useEffect, useRef } from 'react';
import GoogleMapReact from 'google-map-react';
import './css/GoogleMap.css';

const AnyReactComponent = ({ text, id, onClick }) => (
  <div className="custom-marker" onClick={() => onClick(id)}>
    <img src="/pin.png" alt="" />
    {text}
  </div>
);

const GoogleMap = () => {
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [selectedText, setSelectedText] = useState('');
  const mapContainerRef = useRef(null);

  const defaultProps = {
    center: {
      lat: 4.642297536361276,
      lng: -74.09053903031885,
    },
    zoom: 14.2,
    draggable: false, // Disable map dragging
  };

  const markers = [
    {
      id: 1,
      lat: 4.641542013636317,
      lng: -74.09167213103521,
      text: 'Text for Marker 1',
    },
    {
      id: 2,
      lat: 4.639303311132185,
      lng: -74.09792046924814,
      text: 'Text for Marker 2',
    },
    // Add more markers as needed
  ];

  const handleMarkerClick = (markerId) => {
    setSelectedMarker(markerId);
    const selectedMarkerData = markers.find((marker) => marker.id === markerId);
    if (selectedMarkerData) {
      setSelectedText(selectedMarkerData.text);
    }
  };

  const handleMapClick = () => {
    setSelectedMarker(null); // Clear selected marker when clicking on the map
  };

  useEffect(() => {
    // Add a style to freeze the screen
    document.body.style.overflow = 'hidden';

    // Cleanup effect on unmount
    return () => {
      document.body.style.overflow = 'visible';
    };
  }, []);

  useEffect(() => {
    const mapContainer = mapContainerRef.current;
    mapContainer.addEventListener('click', handleMapClick);

    return () => {
      mapContainer.removeEventListener('click', handleMapClick);
    };
  }, []);

  return (
    <div className="map-container" ref={mapContainerRef}>
      <GoogleMapReact
        bootstrapURLKeys={{
          key: 'YOUR_API_KEY',
        }}
        defaultCenter={defaultProps.center}
        defaultZoom={defaultProps.zoom}
        draggable={defaultProps.draggable}
      >
        {markers.map((marker) => (
          <AnyReactComponent
            key={marker.id}
            lat={marker.lat}
            lng={marker.lng}
            id={marker.id}
            text={marker.text}
            onClick={handleMarkerClick}
          />
        ))}
      </GoogleMapReact>

      {selectedMarker !== null && (
        <div className="banner">
          {selectedText}
        </div>
      )}
    </div>
  );
};

export default GoogleMap;
