import React,  { useState } from 'react';
import GoogleMapReact from 'google-map-react';
import './css/GoogleMap.css';

const AnyReactComponent = ({ text, onClick, id }) => (
  <div style={{ position: 'relative' }}>
    <img
      src="/pin.png"
      alt=""
      className="custom-marker"
      onClick={() => onClick(id)}
    />
    {text}
  </div>
);

const GoogleMap = () => {

    const [selectedMarker, setSelectedMarker] = useState(null);

    const defaultProps = {
        center: {
          lat: 4.642297536361276,
          lng: -74.09053903031885,
        },
        zoom: 14.2,
    };


    const markers = [
        {
            id: 1,
            lat: 4.641542013636317,
            lng: -74.09167213103521,
        },
        {
            id: 2,
            lat: 4.639303311132185,
            lng: -74.09792046924814,
        },
        // Add more markers as needed
      ];

  return (
    <div className="map-container">
        <GoogleMapReact
            bootstrapURLKeys={{
              key: 'AIzaSyBoEQde7SsgSK7o3C89akbT1Z6dDl2T6nA',
            }}
            defaultCenter={defaultProps.center}
            defaultZoom={defaultProps.zoom}
          >
            {markers.map((marker) => (
              <AnyReactComponent
                key={marker.id}
                lat={marker.lat}
                lng={marker.lng}
                onClick={setSelectedMarker}
              />
            ))}
        </GoogleMapReact>

        {selectedMarker && (
        <div className="banner">
          Banner for Marker {selectedMarker}
        </div>
      )}
    </div>
  );
};

export default GoogleMap;
