import React, { useState } from 'react';
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';
import axios from "axios";
import {Navigate} from "react-router-dom";
import './css/LoginForm.css';

const apiUrl = process.env.REACT_APP_API_URL;

const LoginFormContainer = () => {
    const [errorMessage, setErrorMessage] = useState('');
    const [showSignup, setShowSignup] = useState(false);
    const [setError] = useState(null);
    const [redirectToSecurePage, setRedirectToSecurePage] = useState(false);

    const toggleForm = () => {
        setShowSignup(!showSignup);
    };

    const handleLogin = (data) => {
        axios
          .post(apiUrl+'/user/auth/login', data)
          .then((response) => {
            console.log(response.data);
            setRedirectToSecurePage(true);
          })
          .catch((error) => {
              if (error.response) {
                  if (error.response.status === 404) {
                      setErrorMessage('The account does not exists');
                  } else if (error.response.status === 401) {
                    setErrorMessage("Unauthorized. Check your credentials.");
                  } else {
                    setErrorMessage("An error occurred. Please try again.");
                  }
                } else if (error.request) {
                  setErrorMessage("No response from the server. Please try again.");
                } else {
                  setErrorMessage("An error occurred. Please try again.");
                }
          });
    };

  const handleSignup = async (formData) => {
      if (formData.password !== formData.confirmPassword) {
          console.log("Error with pass")
          return;
      }

      try {
        const response = await axios.post(apiUrl+'/user/auth/signup', formData);
        console.log(response.data);
        setRedirectToSecurePage(true);
      } catch (error) {
          setError("An error occurred. Please try again.");
      }
  };


  return (
      <div>
          {errorMessage && (
            <div className="error-banner">
                <p className="error-banner-message">{errorMessage}</p>
            </div>
          )}

          <div className={`container forms${showSignup ? ' show-signup' : ''}`}>
              {showSignup ? (
                  <SignupForm
                      onToggleLogin={toggleForm}
                      onSignup={handleSignup}
                  />
              ) : (
                  <LoginForm
                      onToggleSignup={toggleForm}
                      onLogin={handleLogin}
                  />
              )}
              {redirectToSecurePage && <Navigate to="/user" />}
          </div>



      </div>
  );
};

export default LoginFormContainer;
