import React from 'react';
import './LoginForm.css'; // Adjust the path to match your project structure
import FacebookLogin from './FacebookLogin'; // Adjust the import path as needed
import GoogleLogin from './GoogleLogin'; // Adjust the import path as needed
import { useHistory } from 'react-router-dom';
import { withRouter } from 'react-router-dom';

class LoginForm extends React.Component {
  handleLogin = () => {
    // Perform your login logic here
    // If login is successful, you can redirect to another page
    history.push('/user'); // Redirect to the user page
  };

  render() {
    return (
      <section className="container forms">
        <div className="form login">
          <div className="form-content">
            <header>Login</header>
            <form onSubmit={handleLogin}>
              <div className="field input-field">
                <input type="email" placeholder="Email" className="input" />
              </div>

              <div className="field input-field">
                <input type="password" placeholder="Password" className="password" />
                <i className='bx bx-hide eye-icon'></i>
              </div>

              <div className="form-link">
                <a href="#" className="forgot-pass">Forgot password?</a>
              </div>

              <div className="field button-field">
                <button type="submit">Login</button>
              </div>

              {/* Add the Facebook and Google login buttons here */}
              <div className="field button-field">
                <FacebookLogin /> {/* Use your Facebook login component here */}
              </div>
              <div className="field button-field">
                <GoogleLogin /> {/* Use your Google login component here */}
              </div>
            </form>

            <div className="form-link">
              <span>Don't have an account? <a href="#" className="link signup-link">Signup</a></span>
            </div>
          </div>

          <div className="line"></div>
        </div>
      </section>
    );
  }
}

export default withRouter(LoginForm);
