import React, { useState, useEffect } from 'react';

const Comments = () => {
    const [comments, setComments] = useState([]);

    useEffect(() => {
        // Make a GET request to fetch comments from your FastAPI backend.
        fetch('http://your-fastapi-server-url/comments')
            .then((response) => response.json())
            .then((data) => setComments(data))
            .catch((error) => {
                console.error('Error fetching comments:', error);
            });
    }, []);

    return (
        <div className="comments">
            {comments.map((comment) => (
                <div className="media" key={comment.id}>
                    <div className="avatar">
                        <img className="user-avatar" src="/user.png" alt=""/>
                    </div>
                    <div className="comment-content">
                        <div className="comment-header">
                            <h5 className="comment-username">{comment.username}</h5>
                            {/* Use the timestamp from the comment data */}
                            <span className="comment-timestamp">{comment.timestamp}</span>
                        </div>
                        <p className="comment-text">{comment.text}</p>
                        <ul className="comment-actions">
                            <li className="comment-action">
                                <a href="#!"><i className="fa fa-thumbs-up"></i> {comment.likes}</a>
                            </li>
                            <li className="comment-action">
                                <a href="#!"><i className="fa fa-thumbs-down"></i> {comment.dislikes}</a>
                            </li>
                        </ul>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Comments;
