import React, {useState, useEffect, useRef} from 'react';
import GoogleMapReact from 'google-map-react';
import './css/GoogleMap.css';

const AnyReactComponent = ({text, onClick, id}) => (
    <div style={{position: 'relative'}}>
        <img
            src="/pin.png"
            alt=""
            className="custom-marker"
            onClick={() => onClick(id)}
        />
        {text}
    </div>
);

const GoogleMap = () => {
    const [markers, setMarkers] = useState([]);
    const [selectedMarker, setSelectedMarker] = useState(null);
    const [selectedText, setSelectedText] = useState('');
    const [selectedImages, setSelectedImages] = useState([]);
    const [imageIndex, setImageIndex] = useState(0);

    const mapContainerRef = useRef(null);

    useEffect(() => {
        fetch('http://0.0.0.0:8000/locations')
            .then((response) => response.json())
            .then((data) => {
                setMarkers(data);
            })
            .catch((error) => {
                console.error('Error fetching marker data:', error);
            });
    }, []);

    const defaultProps = {
        center: {
            lat: 4.624474886090981,
            lng: -74.07290498520929,
        },
        zoom: 17.5,
        draggable: false,
    };

    const handleMarkerClick = (markerId) => {
        const selectedMarkerData = markers.find((marker) => marker.id === markerId);
        if (selectedMarkerData) {
            setSelectedMarker(markerId);
            setSelectedText(selectedMarkerData.text);
            setSelectedImages(selectedMarkerData.images);
            setImageIndex(0);
        }
    };

    const handleMapClick = (e) => {
        if (mapContainerRef.current && !mapContainerRef.current.contains(e.target)) {
            setSelectedMarker(null);
        }
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Escape') {
            setSelectedMarker(null);
        }
    };

    useEffect(() => {
        document.body.style.overflow = selectedMarker !== null ? 'hidden' : 'visible';
        window.addEventListener('keydown', handleKeyDown);

        return () => {
            document.body.style.overflow = 'visible';
            window.removeEventListener('keydown', handleKeyDown);
        };
    }, [selectedMarker]);

    const handleImagePrev = () => {
        setImageIndex((prevIndex) => (prevIndex - 1 + selectedImages.length) % selectedImages.length);
    };

    const handleImageNext = () => {
        setImageIndex((prevIndex) => (prevIndex + 1) % selectedImages.length);
    };

    return (
        <div className="map-container" ref={mapContainerRef} onClick={handleMapClick}>
            <GoogleMapReact
                bootstrapURLKeys={{
                    key: 'AIzaSyBoEQde7SsgSK7o3C89akbT1Z6dDl2T6nA',
                }}
                defaultCenter={defaultProps.center}
                defaultZoom={defaultProps.zoom}
                draggable={defaultProps.draggable}
            >
                {markers.map((marker) => (
                    <AnyReactComponent
                        key={marker.id}
                        lat={marker.lat}
                        lng={marker.lng}
                        id={marker.id}
                        onClick={handleMarkerClick}
                    />
                ))}
            </GoogleMapReact>

            {selectedMarker !== null && (
                <div className="banner">
                    {selectedText}
                    <div className="image-slider">
                      {/* eslint-disable-next-line jsx-a11y/img-redundant-alt */}
                        <img src={selectedImages[imageIndex]} alt="Image"/>
                        <div className="image-slider-icon">
                            <img
                                src="/before.png"
                                alt="Previous"
                                onClick={handleImagePrev}
                                className="slider-icon"
                            />
                            <img
                                src="/next.png"
                                alt="Next"
                                onClick={handleImageNext}
                                className="slider-icon"
                            />
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default GoogleMap;
