import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './css/GoogleMap.css';

const GoogleMapComponent = () => {
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [selectedText, setSelectedText] = useState('');

  const defaultCenter = {
    lat: 4.642297536361276,
    lng: -74.09053903031885,
  };

  const markers = [
    {
      id: 1,
      lat: 4.641542013636317,
      lng: -74.09167213103521,
      text: 'Text for Marker 1',
    },
    {
      id: 2,
      lat: 4.639303311132185,
      lng: -74.09792046924814,
      text: 'Text for Marker 2',
    },
    // Add more markers as needed
  ];

  const handleMarkerClick = (markerId) => {
    setSelectedMarker(markerId);
    const selectedMarkerData = markers.find((marker) => marker.id === markerId);
    if (selectedMarkerData) {
      setSelectedText(selectedMarkerData.text);
    }
  };

  useEffect(() => {
    // Check if the Google Maps library is loaded
    if (window.google) {
      setGoogleMapLibraryLoaded(true);
    } else {
      // If not loaded, add an event listener to wait for it
      window.addEventListener('google-map-library-loaded', () => {
        setGoogleMapLibraryLoaded(true);
      });
    }
  }, []);

  return (
    <div className="map-container">
      <LoadScript googleMapsApiKey="AIzaSyBoEQde7SsgSK7o3C89akbT1Z6dDl2T6nA">
        <GoogleMap
          center={defaultCenter}
          zoom={14.2}
          mapContainerStyle={{
            width: '100%',
            height: '100%',
          }}
        >
          {markers.map((marker) => (
            <Marker
              key={marker.id}
              position={{ lat: marker.lat, lng: marker.lng }}
              icon={{
                url: '/pin.png', // Your custom marker image
                scaledSize: new window.google.maps.Size(50, 50),
              }}
              onClick={() => handleMarkerClick(marker.id)}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {selectedMarker !== null && (
        <div className="banner">
          {selectedText}
        </div>
      )}
    </div>
  );
};

export default GoogleMapComponent;
