import React, { useState, useEffect } from 'react';
import GoogleMapReact from 'google-map-react';
import './css/GoogleMap.css';

const AnyReactComponent = ({ text, onClick, id }) => (
  <div style={{ position: 'relative' }}>
    <img
      src="/pin.png"
      alt=""
      className="custom-marker"
      onClick={() => onClick(id)}
    />
    {text}
  </div>
);

const GoogleMap = () => {
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [selectedText, setSelectedText] = useState('');
  const [selectedImages, setSelectedImages] = useState([]);
  const [imageIndex, setImageIndex] = useState(0);
  const [markers, setMarkers] = useState([]); // State to store marker data

  const defaultProps = {
    center: {
      lat: 4.622419598502559,
      lng: -74.07343617316029,
    },
    zoom: 18.3,
    draggable: false,
  };

  useEffect(() => {
    fetch('http://0.0.0.0:8000/locations')
      .then((response) => response.json())
      .then((data) => {
        setMarkers(data);
      })
      .catch((error) => {
        console.error('Error fetching marker data:', error);
      });
  }, []);

  const handleMarkerClick = (markerId) => {
    const selectedMarkerData = markers.find((marker) => marker.id === markerId);
    if (selectedMarkerData) {
      setSelectedMarker(markerId);
      setSelectedText(selectedMarkerData.text);
      setSelectedImages(selectedMarkerData.images);
      setImageIndex(0);
    }
  };

  const handleMapClick = () => {
    setSelectedMarker(null);
    setSelectedText('');
    setSelectedImages([]);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Escape') {
      handleMapClick();
    }
  };

  useEffect(() => {
    document.body.style.overflow = selectedMarker !== null ? 'hidden' : 'visible';

    document.addEventListener('keydown', handleKeyPress);

    return () => {
      document.body.style.overflow = 'visible';
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [selectedMarker]);

  return (
    <div className="map-container" onClick={handleMapClick}>
      <GoogleMapReact
        bootstrapURLKeys={{
          key: 'AIzaSyBoEQde7SsgSK7o3C89akbT1Z6dDl2T6nA',
        }}
        defaultCenter={defaultProps.center}
        defaultZoom={defaultProps.zoom}
        draggable={defaultProps.draggable}
      >
        {markers.map((marker) => (
          <AnyReactComponent
            key={marker.id}
            lat={marker.lat}
            lng={marker.lng}
            id={marker.id}
            onClick={handleMarkerClick}
          />
        ))}
      </GoogleMapReact>

      {selectedMarker !== null && (
        <div className="banner">
          {selectedText}
          <div className="image-slider">
            {/* eslint-disable-next-line jsx-a11y/img-redundant-alt */}
            <img src={selectedImages[imageIndex]} alt="Image" />
            <button onClick={() => setImageIndex((prevIndex) => (prevIndex - 1 + selectedImages.length) % selectedImages.length)}>
              Previous
            </button>
            <button onClick={() => setImageIndex((prevIndex) => (prevIndex + 1) % selectedImages.length)}>
              Next
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default GoogleMap;
