/* global gapi */

import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider } from '@react-oauth/google';
import { Navigate } from 'react-router-dom';
import './CustomGoogleButton.css'; // Import the custom styles

const GoogleLoginComponent = () => {
  const [redirectToSecurePage, setRedirectToSecurePage] = useState(false);

  const responseGoogle = (response) => {
    console.log(response);
    fetch('http://localhost:8000/user/auth/google?token=' + response.credential, {
      credentials: 'include',
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);

        if (data && data.secure_data) {
          console.log("Good");
          setRedirectToSecurePage(true);
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  useEffect(() => {
    // Load the Google API scripts here (if not already loaded)
    if (!window.gapi) {
      const script = document.createElement('script');
      script.src = 'https://apis.google.com/js/platform.js';
      script.async = true;
      script.defer = true;
      script.onload = initGoogleSignIn;
      document.head.appendChild(script);
    } else {
      initGoogleSignIn();
    }
  }, []);

  const initGoogleSignIn = () => {
    // Initialize the Google API and attach the custom button
    gapi.load('auth2', function () {
      gapi.auth2.init({
        client_id: 'YOUR_CLIENT_ID.apps.googleusercontent.com',
      });
    });
    attachCustomSignIn(document.getElementById('custom-google-button'));
  };

  const attachCustomSignIn = (element) => {
    gapi.load('signin2', function () {
      gapi.signin2.render('custom-google-button', {
        'width': 190,
        'height': 42,
        'longtitle': true,
        'theme': 'dark',
        'onsuccess': responseGoogle,
        'onfailure': onFailure,
      });
    });
  };

  const onFailure = (error) => {
    console.log(error);
  };

  return (
    <div className="google-login-component">
      <div id="custom-google-button" className="custom-google-button">
        {/* The custom Google Sign-In button will be rendered here */}
        <span className="icon"></span>
        <span className="buttonText">Google</span>
      </div>
      {redirectToSecurePage && <Navigate to="/user" />}
    </div>
  );
};

export default GoogleLoginComponent;
