import React, {useState, useEffect} from 'react';

const getRelativeTime = (timestamp) => {
    const currentDate = new Date();
    const commentDate = new Date(timestamp);

    const timeDifference = currentDate - commentDate;
    const seconds = Math.floor(timeDifference / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);
    const months = Math.floor(days / 30);
    const years = Math.floor(months / 12);

    if (years > 0) {
        return `${years} ${years === 1 ? 'year' : 'years'} ago`;
    } else if (months > 0) {
        return `${months} ${months === 1 ? 'month' : 'months'} ago`;
    } else if (days > 0) {
        return `${days} ${days === 1 ? 'day' : 'days'} ago`;
    } else if (hours > 0) {
        return `${hours} ${hours === 1 ? 'hour' : 'hours'} ago`;
    } else if (minutes > 0) {
        return `${minutes} ${minutes === 1 ? 'minute' : 'minutes'} ago`;
    } else {
        return `${seconds} ${seconds === 1 ? 'second' : 'seconds'} ago`;
    }
};

const Comments = () => {
    const [comments, setComments] = useState([]);
    const [comment, setComment] = useState('');

    useEffect(() => {
        fetch('http://0.0.0.0:8000/comments')
            .then((response) => response.json())
            .then((data) => setComments(data))
            .catch((error) => {
                console.error('Error fetching comments:', error);
            });
    }, []);

    const handleSubmitComment = () => {
        // Add your logic to submit the comment
    };

    return (
        <div className="comments">
            <div className="comment-scroll">
                {comments.map((comment) => (
                    <div className="media" key={comment.id}>
                        <div className="user-avatar">
                            <div className="avatar">
                                {comment.username.substring(0, 2).toUpperCase()}
                            </div>
                        </div>

                        <div className="comment-content">
                            <div className="comment-header">
                                <h5 className="comment-username">{comment.username}</h5>
                                <span className="comment-timestamp">
                  {getRelativeTime(comment.timestamp)}
                </span>
                            </div>
                            <p className="comment-text">{comment.text}</p>
                            <ul className="comment-actions">
                                <li className="comment-action">
                                    <a href="#!">
                                        <i className="fa fa-thumbs-up"></i> {comment.likes}
                                    </a>
                                </li>
                                <li className="comment-action">
                                    <a href="#!">
                                        <i className="fa fa-thumbs-down"></i> {comment.dislikes}
                                    </a>
                                </li>
                            </ul>
                        </div>
                    </div>
                ))}
            </div>
            <div className="comment-input-container">
                <input
                    type="text"
                    className="comment-input"
                    placeholder="Add a comment"
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                />
                <button className="comment-submit-button" onClick={handleSubmitComment}>
                    <i className="fa fa-paper-plane"></i> Submit
                </button>
            </div>
        </div>
    );
};

export default Comments;
