import React, { useRef, useState } from 'react';
import FacebookLogin from './FacebookLogin';
import GoogleLogin from './GoogleLogin';
import './css/LoginForm.css';
import ErrorPopup from "./ErrorPopup";
import { Navigate } from 'react-router-dom';

const LoginForm = ({ onToggleSignup, onLogin, onGoogleLogin, onFacebookLogin }) => {
  const emailRef = useRef();
  const passwordRef = useRef();
  const [errorModalOpen, setErrorModalOpen] = useState(false); // Manage error modal state
  const [modalErrorMessage, setModalErrorMessage] = useState(''); // Store error message for the modal

  const isEmailValid = (email) => {
    // Regular expression for email validation
    const emailRegex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;
    return emailRegex.test(email);
  };

  const closeErrorModal = () => {
    setErrorModalOpen(false);
  };


  const handleSubmit = (e) => {
    e.preventDefault();

    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    if (!email || !password) {
      setModalErrorMessage('All fields are required');
    } else if (!isEmailValid(email)) {
      setModalErrorMessage('Invalid email format');
    } else {
      setModalErrorMessage('');
      onLogin({ email, password });
    }
    setErrorModalOpen(true);
  };

  return (
    <div className="form login">
      <ErrorPopup
        isOpen={errorModalOpen}
        onRequestClose={closeErrorModal}
        errorMessage={modalErrorMessage}
      />
      <div className="form-content">
        <header>Login</header>
        <form onSubmit={handleSubmit}>
          <div className="field input-field">
            <input type="email" placeholder="Email" className="input" ref={emailRef} />
          </div>
          <div className="field input-field">
            <input type="password" placeholder="Password" className="password" ref={passwordRef} />
            <i className='bx bx-hide eye-icon'></i>
          </div>
          <div className="form-link">
            <span>
              Don't have an account?{' '}
              <button onClick={onToggleSignup} className="link signup-link">
                Signup
              </button>
            </span>
          </div>
          {error && <p className="error-message">{error}</p>}
          <div className="field button-field">
            <button type="submit">Login</button>
          </div>
        </form>
      </div>
      <div className="line"></div>
      <div>
        <FacebookLogin />
      </div>
      <div>
        <GoogleLogin />
      </div>
    </div>
  );
};

export default LoginForm;
